import { getRequest, getCategories, saveRequest } from '../api/request';
import { LIST_CATEGORIES, LIST_REQUEST, REQUEST_FAILURE } from '../store/types';

export const requestModule = {
    state () {
        return { 
            listRequest:[],
            categories:[],
            message:''
        }
    },
    mutations: {
        setRequest(state, data){
           state.listRequest = data 
        },
        listCategories(state, data) {
            state.categories = data
        }
        , 
        setRequestFailure(state, data) {
            state.message = data
        }
    },
    actions: {
      async getRequests({commit,state}) {

          getRequest().then(response => {
                if (state.request.listRequest) { 
                    commit(LIST_REQUEST,[...state.request.listRequest,...response.data])
                } else {
                    commit(LIST_REQUEST,[...response.data])
                }
            }).catch(() => [])

        },

        async getCategories() {
            getCategories().then(response => {
                commit(LIST_CATEGORIES,response.data.data)
            }).catch(() => [])
        },

        async sentRequest ({ commit  }, data) {
            return new Promise((resolve, reject) => {
                saveRequest(data).then(response => {
            
                    setTimeout(() => {
                        resolve('success')
                    },300);
                }).catch(err => {
                  
                    commit(REQUEST_FAILURE, 'Failed to save request.');
                    setTimeout(() => {
                        reject('error')
                    },300);

                })
            })
      
        },
    },

    getters : {

    }
};
