import { getRequest, getCategories } from '../api/request';
import { LIST_REQUEST } from '../store/types';

export const requestModule = {
    state () {
        return { 
            listRequest:[],
            categories:[]
        }
    },
    mutations: {
        setRequest(state, data){
           state.listRequest = data 
        },
        listCategories(state, data) {
            state.categories = data
        }
    },
    actions: {
      async getRequests({commit,state}) {

          getRequest().then(response => {
                if (state.request.listRequest) { 
                    commit(LIST_REQUEST,[...state.request.listRequest,...response.data])
                } else {
                    commit(LIST_REQUEST,[...response.data])
                }
            }).catch(() => [])

        },

        async getCategories() {
            getCategories().then(response => {
                commit(LIST_CATEGORIES,response.data.data)
            }).catch(() => [])
        }
    },

    getters : {

    }
};
