import { createStore } from 'vuex';

export default createStore({
    state () {
        return {
            isAuthenticated: false
        }
        
    },
    mutations: {
        setAuthenticate(auth) {
            state.isAuthenticated = auth;
        }
    },
    actions: {},
    modules: {},
});
