export const userModule = {
    state () {
        return {
            isAuthenticated: false,
            dataLogin:{
                email:'',
                password:''
            }
        }
        
    },
    mutations: {
        authenticate(state) {
            state.isAuthenticated = !state.isAuthenticated;
        },
        updateLoginInput (state,inputName, value) {
            state.dataLogin[inputName] = value
        }
    },
    actions: {},

    getters : {
        
    }
};
