export const userModule = {
    state () {
        return {
            isAuthenticated: false,
            dataLogin:{}
        }
        
    },
    mutations: {
        authenticate(state) {
            state.isAuthenticated = !state.isAuthenticated;
        },
        updateLoginInput (state, message) {
            state.dataLogin.message = message
        }
    },
    actions: {},

    getters : {
        
    }
};
