import { getRequest } from '../api/request';
import { LIST_REQUEST } from '../store/types';

export const requestModule = {
    state () {
        return { 
            listRequest:[]
        }
    },
    mutations: {
        setRequest(state, data){
           state.listRequest = data 
        }
    },
    actions: {
        getRequests({commit,state}) {

            getRequest().then(response => {

                if (list) { 
                    commit(LIST_REQUEST,[...state.request.listRequest,...response.data])
                } else {
                    commit(LIST_REQUEST,[...response.data])
                }

            }).catch(() => [])

        }
    },

    getters : {

    }
};
