export const userModule = {
    state () {
        return {
            isAuthenticated: false,
            obj:{ }
        }
        
    },
    mutations: {
        authenticate(state) {
            state.isAuthenticated = !state.isAuthenticated;
        },
        updateLoginInput (state,inputName, value) {
            switch (inputName) {
                case 'email' :
                    state.obj.email = value
                    break;
                case 'password':
                    state.obj.password = value
                    break;
                default:
                    break;
            }
            
        },

        loginUser(state,data) {
           
