import {TOKEN_USER, ISAUTHENTICATE, LOGIN_DATA} from './types';
import { login } from '../api/user';

export const userModule = {
    state () {
        return {
            isAuthenticated: false,
            token: '',
            loginData:{ },
            message:''
        }
        
    },
    mutations: {
        authenticate(state) {
            state.isAuthenticated = !state.isAuthenticated;
        },
        updateLoginInput (state,inputName, value) {
            switch (inputName) {
                case 'email' :
                    state.loginData.email = value
                    break;
                case 'password':
                    state.loginData.password = value
                    break;
                default:
                    break;
            }
            
        },

        setToken(state, data) {
            state.token = data.token
        },

        setLoginData(state, data) {
            state.loginData = data
        },
        loginFailure(state, data) {
            state.message = data
        }

    },
    actions: {
        loginUser ({ commit  }, data) {
            login(data).then(response => {
                commit(TOKEN_USER, response.data.token)
                commit(ISAUTHENTICATE)
                commit(LOGIN_DATA, response.data)
            }).catch(err => {
                console.log(err);
                if (err.status === 401) {
                    commit(LOGIN_FAILURE, 'Failed to login user')
                }
            })
        }

    },

    getters : {
     
    }
};
