import {LOGIN_USER, TOKEN_USER} from './types';
import { login } from '../api/user';

export const userModule = {
    state () {
        return {
            isAuthenticated: false,
            token: '',
            loginData:{ }
        }
        
    },
    mutations: {
        authenticate(state) {
            state.isAuthenticated = !state.isAuthenticated;
        },
        updateLoginInput (state,inputName, value) {
            switch (inputName) {
                case 'email' :
                    state.loginData.email = value
                    break;
                case 'password':
                    state.loginData.password = value
                    break;
                default:
                    break;
            }
            
        },

        setToken(state, data) {
            state.token = data.token
        },


    },
    actions: {
        loginUser ({ commit , state }, data) {
            console.log(state)

            login(data).then(response => {
                commit(TOKEN_USER, response.token)
                commit('authenticate', state)
            }).catch(err => {
                commit(TOKEN_USER, response.token)
            })
            
        }

    },

    getters : {
     
    }
};
