export const userModule = {
    state () {
        return {
            isAuthenticated: false
        }
        
    },
    mutations: {
        authenticate(state) {
            state.user.isAuthenticated = !state.isAuthenticated;
        },
        updateLoginInput (state,inputName, value) {
            switch (inputName) {
                case 'email' :
                    state.user.obj.email = value
                    break;
                case 'password':
                    state.user.obj.email = value
                    break;
                default:
                    break;
            }
            
        }
    },
    actions: {},

    getters : {
        
    }
};
